sys_script_include.script:
var needItUtils = Class.create();
needItUtils.prototype = {
    initialize: function() {
    },
	isDatePast:function(strDate){
		var rightNow = new GlideDateTime();
		var whenRequested = new GlideDateTime(strDate);
		if(whenRequested.before(rightNow)){
			return true;

		}
		else {
			return false;
		}
	},
	isDateToday:function(strDate){
		var rightNow = new GlideDateTime();
		var whenNeeded = new GlideDateTime(strDate);
		if(rightNow.getLocalDate().compareTo(whenNeeded.getLocalDate())==0){
			return true;
		}
		else return false;
	},

    type: 'needItUtils'
};
